import React, { Component } from 'react';
import profilePicturePlaceholder from './img/profilePicturePlaceholder.jpg';
import './App.css';
import queryString from 'query-string';
import $ from 'jquery';

let fakeServerData = {
  user: {
    device_id: '',
    name: 'Cole'
  },
  posts:
    [{
      name: 'Bob',
      text: 'John spelled feeling wrong lol',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz',
      startTime: '110000'
    },
    {
      name: 'John',
      text: 'Felling Good!',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz',
      startTime: '100000'
    },
    {
      name: 'Alex',
      text: 'I\'m Alex',
      songId: '0yBylQ4liLmENeK82vnHOb',
      songName: 'Dont Ask Cuz I Dont Know',
      songArtist: 'Jon Bellion',
      startTime: '100000'
    },
    {
      name: 'Emily',
      text: 'HI!',
      songId: '6ZzYETKetIfNUsZUb23jgG',
      songName: 'Day 1',
      songArtist: 'Honne',
      startTime: '100000'
    }]
}

class PostInfo extends Component {
  render() {
    let name = this.props.name;
    let text = this.props.text;
    return (
      <div className="postInfo">
        <img className="profilePic" src={profilePicturePlaceholder}></img>
        <div>
          <h3 className="username">{name}</h3>
          <p className="postText">{text}</p>
        </div>
      </div>
    );
  }
}

class PostMusic extends Component {
  constructor(props) {
    super(props);
    this.state = {
      picLoaded: false,
      albumUrl: ''
    };
  }
  loadTrack = async () => {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    const trackData = await getTrack(accessToken, this.props.songId);
    console.log('trackData: ');
    console.log(trackData);

    const albumCover = trackData.album.images[0].url;
    this.setState({
      picLoaded: true,
      albumUrl: albumCover
    });
  }
  render() {
    let songName = this.props.songName;
    let songArtist = this.props.songArtist;
    if (!this.state.picLoaded) {
      this.loadTrack();
    }
    return (
      <div className="postMusic">
        <img className="albumImg" src={this.state.picLoaded ? this.state.albumUrl : profilePicturePlaceholder}></img>
        <h4 className="songName">{songName}</h4>
        <p className="artists">{songArtist}</p>
      </div>
    );
  }
}

class Post extends Component {
  constructor(props) {
    super(props);
    this.handleMouseHover = this.handleMouseHover.bind(this);
    this.handleMouseLeave = this.handleMouseLeave.bind(this);
    this.state = {
      playing: false
    };
  }
  handleMouseHover() {
    console.log("HOVER");
    playAt(this.props.deviceId, this.props.accessToken, this.props.post.songId, this.props.post.startTime);
    this.setState({
      playing: true
    });
  }
  handleMouseLeave() {
    console.log("Leave");
    pause(this.props.accessToken);
    this.setState({
      playing: false
    });
  }
  render() {
    let post = this.props.post;

    return (
      <div className="post" onMouseEnter={this.handleMouseHover} onMouseLeave={this.handleMouseLeave}>
        <PostInfo name={post.name} text={post.text} />
        <PostMusic songName={post.songName} songArtist={post.songArtist} songId={post.songId} />
      </div>
    );
  }
}

async function playAt(device_id, token, songId, startTime) {
  $.ajax({
    url: "https://api.spotify.com/v1/me/player/play?device_id=" + device_id,
    type: "PUT",
    data: '{"uris": ["' + 'spotify:track:' + songId + '"], "position_ms": ' + startTime + '}',
    beforeSend: function (xhr) { xhr.setRequestHeader('Authorization', 'Bearer ' + token); },
    success: function (data) {
      console.log(data);
    }
  });
}

async function getDevices(token) {
  return fetch('https://api.spotify.com/v1/me/player/devices', {
    headers: {
      'Authorization': 'Bearer ' + token,
      'Content-Type': 'application/json',
    },
    method: 'GET',
  }).then(d => d.json());
}

async function pause(token) {
  return fetch('https://api.spotify.com/v1/me/player/pause', {
    headers: {
      'Authorization': 'Bearer ' + token,
      'Content-Type': 'application/json',
    },
    method: 'PUT',
  });
}

async function getTrack(token, trackId) {
  const url = 'https://api.spotify.com/v1/tracks/' + trackId;
  return fetch(url, {
    headers: {
      'Authorization': 'Bearer ' + token,
      'Content-Type': 'application/json',
    },
    method: 'GET',
  }).then(d => d.json());
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      serverData: {},
      deviceId: ''
    };
  }
  handlePlayerStatus = async (device_id) => {
    this.setState({
      deviceId: device_id,
    });
  };

  // play = async (accessToken) => {
  //   const devices = await getDevices(accessToken);
  //   if (devices.devices.length > 0) {
  //     let device_id = devices.devices[0].id;
  //     await playAt(device_id, accessToken);
  //   }
  // }

  initializePlayer = () => {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    const name = 'cole';

    this.player = new window.Spotify.Player({
      getOAuthToken: cb => {
        cb(accessToken);
      },
      name
    });

    this.player.addListener('initialization_error', ({ message }) => { console.error(message); });
    this.player.addListener('authentication_error', ({ message }) => { console.error(message); });
    this.player.addListener('account_error', ({ message }) => { console.error(message); });
    this.player.addListener('playback_error', ({ message }) => { console.error(message); });

    // Playback status updates
    this.player.addListener('player_state_changed', state => { console.log(state); });

    // Ready
    this.player.addListener('ready', ({ device_id }) => {
      this.handlePlayerStatus(device_id);
      console.log('Ready with Device ID', device_id);
    });

    // Not Ready
    this.player.addListener('not_ready', ({ device_id }) => {
      console.log('Device ID has gone offline', device_id);
    });

    this.player.connect();
  };

  componentDidMount() {
    window.onSpotifyWebPlaybackSDKReady = this.initializePlayer;
    this.setState({
      serverData: fakeServerData
    });
  }
  render() {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;

    let allPosts =
      (this.state.serverData.user && this.state.serverData.posts) ?
        this.state.serverData.posts : [];
    // this.play(accessToken)

    return (
      <div className="App">
        {
          (this.state.serverData.user && this.state.deviceId) &&
          <div>
            <h1>{this.state.serverData.user.name}</h1>
            {allPosts.map(currentPost => <Post post={currentPost} deviceId={this.state.deviceId} accessToken={accessToken}/>)}
          </div>
        }
      </div>
    );
  }
}

export default App;
