import React, { Component } from 'react';
import profilePicturePlaceholder from './img/profilePicturePlaceholder.jpg';
import './App.css';
import queryString from 'query-string';
import $ from 'jquery';

let fakeServerData = {
  deviceid: '',
  user: {
    name: 'Cole'
  },
  posts:
    [{
      name: 'Bob',
      text: 'John spelled feeling wrong lol',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz'
    },
    {
      name: 'John',
      text: 'Felling Good!',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz'
    },
    {
      name: 'Alex',
      text: 'I\'m Alex',
      songId: '0yBylQ4liLmENeK82vnHOb',
      songName: 'Dont Ask Cuz I Dont Know',
      songArtist: 'Jon Bellion'
    },
    {
      name: 'Emily',
      text: 'HI!',
      songId: '6ZzYETKetIfNUsZUb23jgG',
      songName: 'Day 1',
      songArtist: 'Honne'
    }]
}

// function Header() {
//   return (
//     <div className="header">
//       <h1>{this.state.serverData.user && this.state.serverData.user.name}</h1>
//     </div>
//   );
// }

class PostInfo extends Component {
  render() {
    let name = this.props.name;
    let text = this.props.text;
    return (
      <div className="postInfo">
        <img className="profilePic" src={profilePicturePlaceholder}></img>
        <div>
          <h3 className="username">{name}</h3>
          <p className="postText">{text}</p>
        </div>
      </div>
    );
  }
}

class PostMusic extends Component {
  render() {
    let songName = this.props.songName;
    let songArtist = this.props.songArtist;
    let songId = this.props.songId;
    return (
      <div className="postMusic">
        <img className="albumImg" src={profilePicturePlaceholder}></img>
        <h4 className="songName">{songName}</h4>
        <p className="artists">{songArtist}</p>
      </div>
    );
  }
}

class Post extends Component {
  render() {
    let post = this.props.post;
    return (
      <div className="post">
        <PostInfo name={post.name} text={post.text} />
        <PostMusic songName={post.songName} songArtist={post.songArtist} songId={post.songId} />
      </div>
    );
  }
}
function playAt() {
  $.ajax({
    url: "https://api.spotify.com/v1/me/player/play?device_id=" + window.device_id,
    type: "PUT",
    data: '{"uris": ["' + 'spotify:track:0d28khcov6AiegSCpG5TuT' + '"], "position_ms": ' + '100000' + '}',
    beforeSend: function (xhr) { xhr.setRequestHeader('Authorization', 'Bearer ' + 'BQC-dQntQfPIR5lLP8D6vWWWtmNZd6ejCzX0Lt7RDdA_Rs3IVTUuXYFquNyAUF6OroQ1WVsOPyKtA2UrxfoAHnNtq53OKZaVrA0H0mqSrUZEDwGVhe0XPXkWOSaSpdGRJfh3MY8cuCgMOOlDlGP2hi9i7k0Tkf7mbGtPtYc'); },
    success: function (data) {
      console.log(data);
    }
  });
}
// render() {
//   this.playAt(100000);
//   return (
//       <div>
//           <h3 id="username">Test User</h3>
//           <p id="post">Wow! something something somethnig</p>
//       </div>
//   );
// }

class App extends Component {
  constructor() {
    super();
    this.state = { serverData: {} };
  }
  componentDidMount() {
    fakeServerData.deviceid = window.device_id;
    this.setState({
      serverData: fakeServerData,
    });
  }
  render() {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    let allPosts =
      (this.state.serverData.user && this.state.serverData.posts) ?
        this.state.serverData.posts : [];
    return (
      <div className="App">
        {
          this.state.serverData.user &&
          <div>
            <h1>{this.state.serverData.user.name}</h1>
            {allPosts.map(currentPost => <Post post={currentPost} />)}
            {
              playAt()
            }
          </div>
        }
      </div>
    );
  }
}

export default App;
