import React, { Component } from 'react';
import profilePicturePlaceholder from '../img/profilePicturePlaceholder.jpg';
import '../style/App.css';
import queryString from 'query-string';
import $ from 'jquery';
import Post from './Post';
import NewPost from './NewPost';
import { loadPosts } from '../backendCalls';
import { getTrack, playAt, pause, search } from '../spotifyCalls';

let fakeServerData = {
  user: {
    device_id: '',
    name: 'Cole'
  },
  posts:
    [{
      name: 'Bob',
      text: 'John spelled feeling wrong lol',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz',
      startTime: '122500'
    },
    {
      name: 'John',
      text: 'Felling Good!',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz',
      startTime: '100000'
    },
    {
      name: 'Alex',
      text: 'I\'m Alex',
      songId: '0yBylQ4liLmENeK82vnHOb',
      songName: 'Dont Ask Cuz I Dont Know',
      songArtist: 'Jon Bellion',
      startTime: '100000'
    },
    {
      name: 'Emily',
      text: 'HI!',
      songId: '6ZzYETKetIfNUsZUb23jgG',
      songName: 'Day 1',
      songArtist: 'Honne',
      startTime: '100000'
    }]
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      serverData: {},
      deviceId: '',
      newPost: false,
      time: 0,
      start: 0
    };
    this.clickOuterNewPost = this.clickOuterNewPost.bind(this);
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
  }

  handlePlayerStatus = async (device_id) => {
    this.setState({
      deviceId: device_id,
    });
  };

  // play = async (accessToken) => {
  //   const devices = await getDevices(accessToken);
  //   if (devices.devices.length > 0) {
  //     let device_id = devices.devices[0].id;
  //     await playAt(device_id, accessToken);
  //   }
  // }

  initializePlayer = () => {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    const name = 'vibin';

    this.player = new window.Spotify.Player({
      getOAuthToken: cb => {
        cb(accessToken);
      },
      name
    });

    this.player.addListener('initialization_error', ({ message }) => { console.error(message); });
    this.player.addListener('authentication_error', ({ message }) => { console.error(message); });
    this.player.addListener('account_error', ({ message }) => { console.error(message); });
    this.player.addListener('playback_error', ({ message }) => { console.error(message); });

    // Playback status updates
    this.player.addListener('player_state_changed', state => { console.log(state); });

    // Ready
    this.player.addListener('ready', ({ device_id }) => {
      this.handlePlayerStatus(device_id);
      console.log('Ready with Device ID', device_id);
    });

    // Not Ready
    this.player.addListener('not_ready', ({ device_id }) => {
      console.log('Device ID has gone offline', device_id);
    });

    this.player.connect();
  };

  componentDidMount() {
    window.onSpotifyWebPlaybackSDKReady = this.initializePlayer;
    loadPosts().then(post => this.setState({
      serverData: {
        posts: post,
        user: {
          name: 'Cole'
        }
      }
    }));
  }

  clickOuterNewPost() {
    const showPost = this.state.newPost;
    this.setState({
      newPost: !showPost
    });
  }
  pauseSong = async (startTime) => {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    await pause(accessToken);
    this.stopTimer(startTime);
  }
  playSong = async (songId, startTime, endTime) => {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    await playAt(this.state.deviceId, accessToken, songId, startTime);
    this.stopTimer(startTime);
    this.startTimer(startTime, endTime);
  }
  startTimer(startTime, endTime) {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    this.setState({
      time: startTime,
      start: Date.now() - this.state.time
    });
    if (!this.timer) {
      this.timer = setInterval(() => {
        this.setState({
          time: Math.floor(Date.now() - this.state.start)
        });
        console.log("time: " + this.state.time);
        if (this.state.time > endTime) {
          this.pauseSong(accessToken);
          console.log("PAUSE ITTT");
        }
      }, 1000);
    }
  }
  stopTimer(startTime) {
    this.setState({
      time: startTime
    });
    clearInterval(this.timer);
    this.timer = '';
  }
  render() {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;

    let allPosts =
      (this.state.serverData.user && this.state.serverData.posts) ?
        this.state.serverData.posts : [];

    return (
      <div className="App">
        {this.state.newPost &&
          <div>
            <div className="outerNewPost" onMouseDown={this.clickOuterNewPost}></div>
            <NewPost
              deviceId={this.state.deviceId}
              accessToken={accessToken}
              onMouseDown={() => { }}
              userName={this.state.serverData.user.name}
              playSong={this.playSong}
              pauseSong={this.pauseSong}
            />
          </div>
        }
        {
          (this.state.serverData.user && this.state.deviceId) &&
          <div className={this.state.newPost ? "blurPosts" : ""}>
            <h1>{this.state.serverData.user.name}</h1>
            <button onClick={() => {
              const showPost = this.state.newPost;
              this.setState({
                newPost: !showPost
              });
            }}>{this.state.newPost ? "Cancel" : "New Post"}</button>
            <div className="posts">
              {allPosts.map(currentPost =>
                <Post
                  post={currentPost}
                  deviceId={this.state.deviceId}
                  accessToken={accessToken}
                  playSong={this.playSong}
                  pauseSong={this.pauseSong}
                />)}
            </div>
          </div>
        }
      </div>
    );
  }
}

export default App;
