import React, { Component } from 'react';
import profilePicturePlaceholder from './img/profilePicturePlaceholder.jpg';
import './App.css';
import queryString from 'query-string';
import $ from 'jquery';

let fakeServerData = {
  user: {
    device_id: '',
    name: 'Cole'
  },
  posts:
    [{
      name: 'Bob',
      text: 'John spelled feeling wrong lol',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz'
    },
    {
      name: 'John',
      text: 'Felling Good!',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz'
    },
    {
      name: 'Alex',
      text: 'I\'m Alex',
      songId: '0yBylQ4liLmENeK82vnHOb',
      songName: 'Dont Ask Cuz I Dont Know',
      songArtist: 'Jon Bellion'
    },
    {
      name: 'Emily',
      text: 'HI!',
      songId: '6ZzYETKetIfNUsZUb23jgG',
      songName: 'Day 1',
      songArtist: 'Honne'
    }]
}

