import React, { Component } from 'react';
import profilePicturePlaceholder from './img/profilePicturePlaceholder.jpg';
import './App.css';

let fakeServerData = {
  user: {
    name: 'Cole'
  },
  posts:
    [{
      name: 'John',
      text: 'Felling Good!',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz'
    },
    {
      name: 'Alex',
      text: 'I\'m Alex',
      songId: '0yBylQ4liLmENeK82vnHOb',
      songName: 'Dont Ask Cuz I Dont Know',
      songArtist: 'Jon Bellion'
    },
    {
      name: 'Emily',
      text: 'HI!',
      songId: '6ZzYETKetIfNUsZUb23jgG',
      songName: 'Day 1',
      songArtist: 'Honne'
    },
    {
      name: 'Bob',
      text: 'John spelled feeling wrong lol',
      songId: '0d28khcov6AiegSCpG5TuT',
      songName: 'Feel Good Inc.',
      songArtist: 'Gorrilaz'
    },]
}

// function Header() {
//   return (
//     <div className="header">
//       <h1>{this.state.serverData.user && this.state.serverData.user.name}</h1>
//     </div>
//   );
// }

function PostInfo() {
  return (
    <div className="postInfo">
      <img className="profilePic" src={profilePicturePlaceholder}></img>
      <div>
        <h3 className="username">Test User</h3>
        <p className="postText">Wow! something something somethnig</p>
      </div>
    </div>
  );
}

function PostMusic() {
  return (
    <div className="postMusic">
      <img className="albumImg" src={profilePicturePlaceholder}></img>
      <h4 className="songName">This Song</h4>
      <p className="artists">Singer</p>
    </div>
  );
}

function Post() {
  let post = this.props.post;
  return (
    <div className="post">
      <PostInfo name={this.props.post.name} text={this.props.post.text}/>
      <PostMusic />
    </div>
  );
}

class App extends Component {
  constructor() {
    super();
    this.state = { serverData: {} };
  }
  componentDidMount() {
    this.setState({
      serverData: fakeServerData
    });
  }
  render() {
    let allPosts = 
      (this.state.serverData.user && this.state.serverData.posts) ?
      this.state.serverData.posts : [];
    return (
      <div className="App">
        {
          this.state.serverData.user &&
          <div>
            <h1>{this.state.serverData.user.name}</h1>
            { allPosts.map(currentPost => <Post post={currentPost}/>)}
          </div>
        }
      </div>
    );
  }
}

export default App;
