import React, { Component } from 'react';
import queryString from 'query-string';
import '../style/App.css';
import { loadPosts, loadLikes, loadUserFollowing, followUser, unFollowUser } from '../backendCalls'
import { getLoggedInUser, getUser } from '../spotifyCalls';
import Post from '../components/Post';
import profilePicturePlaceholder from '../img/profilePicturePlaceholder.jpg';

class UserProfile extends Component {
    constructor() {
        super();
        this.state = {
            deviceId: '',
            accessToken: '',
            profilePageUsername: '',
            serverData: {},
            muted: true,
            followers: [],
            following: []
        };
        this.followUser = this.followUser.bind(this);
    }
    handlePlayerStatus = async (device_id) => {
        this.setState({
            deviceId: device_id,
        });
    };
    initializePlayer = () => {
        const name = this.state.serverData.user.name + ' vibes';

        this.player = new window.Spotify.Player({
            getOAuthToken: cb => {
                cb(this.state.accessToken);
            },
            name
        });

        this.player.addListener('initialization_error', ({ message }) => { console.error(message); });
        this.player.addListener('authentication_error', ({ message }) => { console.error(message); });
        this.player.addListener('account_error', ({ message }) => { console.error(message); });
        this.player.addListener('playback_error', ({ message }) => { console.error(message); });

        // Playback status updates
        this.player.addListener('player_state_changed', state => { console.log(state); });

        // Ready
        this.player.addListener('ready', ({ device_id }) => {
            this.handlePlayerStatus(device_id);
            console.log('Ready with Device ID', device_id);
        });

        // Not Ready
        this.player.addListener('not_ready', ({ device_id }) => {
            console.log('Device ID has gone offline', device_id);
        });

        this.player.connect();
    };
    componentDidMount() {
        let parsed = queryString.parse(window.location.search);
        let profilePageUsernameFromUrl = parsed.username;
        let postsForProfile = [];
        if (parsed.access_token) {
            let accessToken = parsed.access_token;
            getUser(accessToken, profilePageUsernameFromUrl).then(user => {
                let imgUrl = user.images.length > 0 ?
                    user.images[0].url : '';
                this.setState({
                    imgUrl: imgUrl
                });
            });
            loadUserFollowing(profilePageUsernameFromUrl).then(user => {
                this.setState({
                    followers: user.followers,
                    following: user.following
                })
            })

            getLoggedInUser(accessToken).then(user => {
                loadPosts().then(posts => {
                    loadLikes().then(likes => {
                        posts.forEach(post => {
                            let likedByProfilePageUser = false;
                            let likeCount = 0
                            if (post.name === profilePageUsernameFromUrl) {
                                likes.forEach(like => {
                                    if (post._id === like.postId) {
                                        likeCount++;
                                    }
                                });
                                post = {
                                    ...post,
                                    likedByProfilePageUser: likedByProfilePageUser,
                                    likeCount: likeCount
                                }
                                postsForProfile.push(post);
                            } else {
                                likes.forEach(like => {
                                    if (post._id === like.postId) {
                                        likeCount++;
                                        if (like.username === profilePageUsernameFromUrl) {
                                            console.log("liked")
                                            post = {
                                                ...post,
                                                likedByProfilePageUser: true,
                                                likeCount: likeCount
                                            }
                                            postsForProfile.push(post);
                                        }
                                    }
                                });
                            }
                        });
                        this.setState({
                            accessToken: accessToken,
                            serverData: {
                                posts: postsForProfile,
                                user: {
                                    name: user.display_name
                                }
                            },
                            profilePageUsername: profilePageUsernameFromUrl
                        });
                    }).then(window.onSpotifyWebPlaybackSDKReady = this.initializePlayer);
                });
            });
        } else {
            loadPosts().then(posts => {
                loadLikes().then(likes => {
                    posts.forEach(post => {
                        let likeCount = 0
                        if (post.name === profilePageUsernameFromUrl) {
                            likes.forEach(like => {
                                if (post._id === like.postId) {
                                    likeCount++;
                                }
                            });
                            post = {
                                ...post,
                                likedByProfilePageUser: false,
                                likeCount: likeCount
                            }
                            postsForProfile.push(post);
                        } else {
                            likes.forEach(like => {
                                if (post._id === like.postId) {
                                    likeCount++;
                                    if (like.username === profilePageUsernameFromUrl) {
                                        post = {
                                            ...post,
                                            likedByProfilePageUser: true,
                                            likeCount: likeCount
                                        }
                                        postsForProfile.push(post);
                                    }
                                }
                            });
                        }
                    });
                    this.setState({
                        serverData: {
                            posts: postsForProfile,
                        },
                        profilePageUsername: profilePageUsernameFromUrl
                    });
                });
            });
        }
    }
    unFollowUser() {
        unFollowUser(this.state.serverData.user.name, this.state.profilePageUsername);
        const updatedFollowerList = this.state.followers;
        updatedFollowerList.push(this.state.serverData.user.name);
        this.setState({
            followers: updatedFollowerList
        });
    }
    followUser() {
        followUser(this.state.serverData.user.name, this.state.profilePageUsername);
        const updatedFollowerList = this.state.followers;
        updatedFollowerList.push(this.state.serverData.user.name);
        this.setState({
            followers: updatedFollowerList
        });
    }
    render() {
        let allPosts = this.state.serverData.posts ? this.state.serverData.posts : [];
        let followers = this.state.followers ? this.state.followers : [];
        let following = this.state.following ? this.state.following : [];
        let name = this.state.serverData.user ? this.state.serverData.user.name : '';
        return (
            <div className='App'>
                <div className='header'>
                    <h1 style={{ 'margin': '.5em' }}>Vibe Share</h1>
                    <div className='buttonContainer'>
                        <button className='myButtonWhite' onClick={this.handleLogInButton}>{this.state.accessToken ? 'Log Out' : 'Log In'}</button>
                    </div>
                </div>
                <div className='profilePageContent'>
                    <div className='profilePageUser'>
                        <img className='profilePageImg' src={this.state.imgUrl ? this.state.imgUrl : ''} />
                        <h2 className='profilePageUsername'>{this.state.profilePageUsername}</h2>
                        <div className='followContainer'>
                            {this.state.serverData.user && (this.state.serverData.user.name !== this.state.profilePageUsername) &&
                                <button className='myButtonBlue' onClick={this.followUser}>Follow</button>
                            }
                            <h3>Followers: {followers.length}</h3>
                            <div className='scrollFollowers'>
                                {followers.map(follower =>
                                    <FollowerLink name={follower} ></FollowerLink>
                                )}
                            </div>
                            <h3>Following: {following.length}</h3>
                            <div className='scrollFollowing'>
                                {following.map(following =>
                                    <FollowerLink name={following} ></FollowerLink>
                                )}
                            </div>
                        </div>
                    </div>
                    <div className='profilePagePosts'>
                        {allPosts.map(currentPost =>
                            <div className='postContainer'>
                                {currentPost.likedByProfilePageUser && <p className="userProfileLikes">{this.state.profilePageUsername} liked:</p>}
                                <Post
                                    post={currentPost}
                                    deviceId={this.state.deviceId}
                                    accessToken={this.state.accessToken}
                                    muted={this.state.muted}
                                    username={name}
                                />
                            </div>
                        )}
                    </div>
                </div>
            </div >
        )
    }
}

export default UserProfile;

class FollowerLink extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imgUrl: '',
            name: '',
        };
        this.redirectToUserProfile = this.redirectToUserProfile.bind(this);
    }
    componentDidMount() {
        let parsed = queryString.parse(window.location.search);
        let accessToken = ''
        if (parsed.access_token) {
            accessToken = parsed.access_token;
            getUser(accessToken, this.props.name).then(user => {
                let imgUrl = user.images ?
                    user.images[0].url : '';
                this.setState({
                    imgUrl: imgUrl,
                });
            });
        }
    }
    redirectToUserProfile() {
        let parsed = queryString.parse(window.location.search);
        let accessToken = ''
        if (parsed.access_token)
            accessToken = parsed.access_token;

        window.location.href = window.location.origin + '/user/?username=' + this.props.name + (accessToken ? ('&access_token=' + accessToken) : '');
    }
    render() {
        let name = this.props.name;
        return (
            <div className="followerLink" onClick={this.redirectToUserProfile}>
                <img src={this.state.imgUrl ? this.state.imgUrl : profilePicturePlaceholder}></img>
                <h3>{name}</h3>
            </div>
        );
    }
}
