import React, { Component } from 'react';
import profilePicturePlaceholder from '../img/profilePicturePlaceholder.jpg';
import '../style/App.css';
import queryString from 'query-string';
import Post from './Post';
import NewPost from './NewPost';
import { loadPosts, submitPost, loadLikes, likePost, deleteLike } from '../backendCalls';
import { getUser } from '../spotifyCalls';

class App extends Component {
  constructor() {
    super();
    this.state = {
      serverData: {},
      deviceId: '',
      accessToken: '',
      newPost: false,
      muted: false
    };
    this.clickOuterNewPost = this.clickOuterNewPost.bind(this);
    this.submitNewPost = this.submitNewPost.bind(this);
    this.handleLogInButton = this.handleLogInButton.bind(this);
  }

  handlePlayerStatus = async (device_id) => {
    this.setState({
      deviceId: device_id,
    });
  };

  initializePlayer = () => {
    const name = this.state.serverData.user.name + ' vibes';

    this.player = new window.Spotify.Player({
      getOAuthToken: cb => {
        cb(this.state.accessToken);
      },
      name
    });

    this.player.addListener('initialization_error', ({ message }) => { console.error(message); });
    this.player.addListener('authentication_error', ({ message }) => { console.error(message); });
    this.player.addListener('account_error', ({ message }) => { console.error(message); });
    this.player.addListener('playback_error', ({ message }) => { console.error(message); });

    // Playback status updates
    this.player.addListener('player_state_changed', state => { console.log(state); });

    // Ready
    this.player.addListener('ready', ({ device_id }) => {
      this.handlePlayerStatus(device_id);
      console.log('Ready with Device ID', device_id);
    });

    // Not Ready
    this.player.addListener('not_ready', ({ device_id }) => {
      console.log('Device ID has gone offline', device_id);
    });

    this.player.connect();
  };

  componentDidMount() {
    let parsed = queryString.parse(window.location.search);
    if (parsed.access_token) {
      let accessToken = parsed.access_token;
      let username = '';
      getUser(accessToken).then(user => {
        username = user.display_name;
        window.username = username;
        loadPosts().then(posts => {
          let postsWithLikeCount = [];
          loadLikes().then(likes => {
            posts.forEach(post => {
              let likeCount = 0;
              likes.forEach(like => {
                if (post._id === like.postId) {
                  likeCount++;
                }
              });
              post = {
                ...post,
                likeCount: likeCount
              }
              postsWithLikeCount.push(post);
            });
            this.setState({
              accessToken: accessToken,
              serverData: {
                posts: postsWithLikeCount,
                user: {
                  name: username
                }
              }
            });
          }).then(window.onSpotifyWebPlaybackSDKReady = this.initializePlayer);
        });
      });
    } else {
      loadPosts().then(posts => {
        let postsWithLikeCount = [];
        loadLikes().then(likes => {
          posts.forEach(post => {
            let likeCount = 0
            likes.forEach(like => {
              if (post._id === like.postId) {
                likeCount++;
              }
            })
            post = {
              ...post,
              likeCount: likeCount
            }
            postsWithLikeCount.push(post);
          })
          this.setState({
            serverData: {
              posts: postsWithLikeCount,
            }
          })
        })
      })
    }
  }

  async submitNewPost(text, songId, songName, songArtist, startTime, stopTime) {
    const user = this.state.serverData.user.name;
    const newPost = await submitPost(
      user,
      text,
      songId,
      songName,
      songArtist,
      startTime,
      stopTime
    );
    const updatedPosts = this.state.serverData.posts;
    updatedPosts.unshift(newPost);
    this.setState({
      serverData: {
        posts: updatedPosts,
        user: this.state.serverData.user
      },
      newPost: false
    });

  }

  clickOuterNewPost() {
    const showPost = this.state.newPost;
    this.setState({
      newPost: !showPost
    });
  }

  handleLogInButton() {
    if (!this.state.accessToken) {
      window.location.href = (window.location.href === 'http://localhost:3000/') ?
        'http://localhost:8000/login' : 'https://vibin-backend.herokuapp.com/login'
    }
  }

  render() {
    let allPosts = this.state.serverData.posts ? this.state.serverData.posts : [];
    let name = this.state.serverData.user ? this.state.serverData.user.name : 'Not logged in';
    let loaded = this.state.serverData.posts;

    return (
      <div className="App">
        <div className='header'>
          <div>
            <h1>Vibe Share</h1>
            <h3 className='usernameTitle'>{name}</h3>
          </div>
          <div className='buttonContainer'>
            {loaded &&
              <button className='myButtonWhite' onClick={() => {
                const showPost = this.state.newPost;
                this.setState({
                  newPost: !showPost
                });
              }}>{this.state.newPost ? "Cancel" : "New Post"}</button>
            }
            <button className='myButtonWhite' onClick={this.handleLogInButton}>{this.state.accessToken ? 'Log Out' : 'Log In'}</button>
          </div>
        </div>
        {this.state.newPost &&
          <div>
            <div className="outerNewPost" onMouseDown={this.clickOuterNewPost}></div>
            <NewPost
              deviceId={this.state.deviceId}
              accessToken={this.state.accessToken}
              onMouseDown={() => { }}
              userName={name}
              submitNewPost={this.submitNewPost}
              handleLogInButton={this.handleLogInButton}
            />
          </div>
        }
        {
          (!this.state.accessToken) &&
          <h3 style={{ 'margin': '6em 0 -4em 5.5em' }}>Log in to share posts and listen to peoples vibes!</h3>
        }

        {
          this.state.accessToken
            ?
            <div className={this.state.newPost ? "blurPosts" : ""}>
              <div className="posts">
              <button className={'myButtonWhite muteButton' + this.state.muted ? 'muted' : ''}
                onClick={this.setState({muted: !this.state.muted})}>
                  Mute
              </button>
                {allPosts.map(currentPost =>
                  <Post
                    post={currentPost}
                    deviceId={this.state.deviceId}
                    accessToken={this.state.accessToken}
                  />)}
              </div>
            </div>
            :
            <div className={"notSignedIn posts" + (this.state.newPost ? " blurPosts" : "")}>
              {allPosts.map(currentPost =>
                <Post
                  post={currentPost}
                  deviceId={null}
                  accessToken={null}
                />)}
            </div>
        }
      </div>
    );
  }
}

export default App;
