export async function getDevices(token) {
  return fetch(`https://api.spotify.com/v1/me/player/devices`, {
    headers: {
      Authorization: `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
    method: 'GET',
  }).then(d => d.json());
}

async function play(accessToken) {
  const devices = await getDevices(accessToken);
  console.log(devices);
  device_id = devices.devices[0].id;
  await playAt(device_id, accessToken);
}

class App extends Component {
  constructor() {
    super();
    this.state = { serverData: {} };
  }
  componentDidMount() {
    // this.setState({
    //   serverData: fakeServerData
    // });
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    console.log(accessToken);
    // setTimeout(
    //   fetch('https://api.spotify.com/v1/me/player/devices', {
    //     headers: { 'Authorization': 'Bearer ' + accessToken }
    //   }).then(response => response.json())
    //     .then(data => console.log(data)), 100000
    // );
    // .then(data => this.setState({serverData: {user: {device_id: data.devices[0].id}}}))
  }
  render() {
    let allPosts =
      (this.state.serverData.user && this.state.serverData.posts) ?
        this.state.serverData.posts : [];
    return (
      <div className="App">
        {
          (this.state.serverData.user && window.device_id) &&
          <div>
            <h1>{this.state.serverData.user.name}</h1>
            {allPosts.map(currentPost => <Post post={currentPost} />)}
            {
              play(accessToken)
            }
          </div>
        }
      </div>
    );
  }
}

export default App;
