import React, { Component } from 'react';
import profilePicturePlaceholder from '../img/profilePicturePlaceholder.jpg';
import '../style/App.css';
import queryString from 'query-string';
import { getTrack, playAt, pause } from '../spotifyCalls';
import { useHistory, Link } from 'react-router-dom';
import { loadLikes, likePost, deleteLike } from '../backendCalls';

class PostInfo extends Component {
    render() {
        let name = this.props.name;
        let text = this.props.text;
        return (
            <div className="postInfo">
                <img className="profilePic" src={profilePicturePlaceholder}></img>
                <div>
                    <h3 className="username">{name}</h3>
                    <p className="postText">{text}</p>
                </div>
            </div>
        );
    }
}

class PostMusic extends Component {
    constructor(props) {
        super(props);
        this.state = {
            picLoaded: false,
            albumUrl: '',
            songName: ''
        };
        this.routeToSpotify = this.routeToSpotify.bind(this);
    }
    routeToSpotify = () => {
        const history = useHistory();
        history.push('https://open.spotify.com/track/' + this.props.songId);
    }
    loadTrack = async () => {
        const trackData = await getTrack(this.props.accessToken, this.props.songId);
        const albumCover = trackData.album.images[0].url;
        this.setState({
            picLoaded: true,
            albumUrl: albumCover,
            songName: this.props.songName
        });
    }
    render() {
        let songName = this.props.songName;
        let songArtist = this.props.songArtist;
        if ((this.props.loggedIn && (this.state.songName != this.props.songName)) || (this.props.loggedIn && !this.state.picLoaded)) {
            this.loadTrack();
        }
        return (
            <div className="postMusic">
                <img className="albumImg"
                    onClick={event => window.location.href = 'https://open.spotify.com/track/' + this.props.songId}
                    src={this.state.picLoaded ? this.state.albumUrl : profilePicturePlaceholder}>
                </img>
                <div className="songCredit">
                    <h4 className="songName">{songName}</h4>
                    <p className="artists">{songArtist}</p>
                </div>
            </div>
        );
    }
}

export default class Post extends Component {
    constructor(props) {
        super(props);
        this.handleMouseHover = this.handleMouseHover.bind(this);
        this.handleMouseLeave = this.handleMouseLeave.bind(this);
        this.clickedLikePost = this.clickedLikePost.bind(this);
        this.state = {
            playing: false,
            time: 0,
            start: 0,
            accessToken: '',
            likeCount: ''
        };
    }
    componentDidMount() {
        this.setState({ accessToken: this.props.accessToken, likeCount: this.props.post.likeCount });
    }
    componentDidUpdate(prevProps) {
        if (this.props.accessToken != prevProps.accessToken)
            this.setState({ accessToken: this.props.accessToken });
    }
    pauseSong = async (startTime) => {
        pause(this.props.accessToken)
        this.stopTimer(startTime);
    }
    playSong = async (songId, startTime, endTime) => {
        playAt(this.props.deviceId, this.props.accessToken, songId, startTime);
        clearInterval(this.timer);
        this.timer = '';
        this.startTimer(startTime, endTime);
    }
    startTimer = async (startTime, endTime) => {
        this.setState({
            time: startTime,
            start: Date.now() - startTime
        });
        if (!this.timer) {
            this.timer = setInterval(() => {
                this.setState({
                    time: Math.floor(Date.now() - this.state.start),
                });
                if (this.state.time > endTime) {
                    this.pauseSong(startTime);
                }
            }, 1000);
        }
    }
    stopTimer = async (startTime) => {
        this.setState({
            time: startTime,
        });
        clearInterval(this.timer);
        this.timer = '';
    }
    handleMouseHover() {
        // this.playSong(this.props.post.songId, this.props.post.startTime, this.props.post.stopTime);
    }
    handleMouseLeave() {
        // setTimeout((() => {
        //     this.pauseSong(this.props.post.startTime);
        // }), 300);
    }
    clickedLikePost() {
        let newLike = true;
        loadLikes().then(likes => {
            likes.forEach(like => {
                if (like.postId === this.props.post._id && like.username === this.props.post.name)
                    newLike = false;
            });
        }).then(() => {
            if (newLike) {
                likePost(this.props.post.name, this.props.post._id);
                this.setState({ likeCount: (this.state.likeCount + 1) })
                console.log(this.state.likeCount++);
            } else {
                deleteLike(this.props.post.name, this.props.post._id);
                this.setState({ likeCount: (this.state.likeCount - 1) })
            }
        });
    }
    render() {
        let post = this.props.post;
        if (this.state.accessToken) {
            return (
                <div className="post" onMouseEnter={this.handleMouseHover} onMouseLeave={this.handleMouseLeave}>
                    <div className="postInner">
                        <PostInfo name={post.name} text={post.text} />
                        <PostMusic songName={post.songName} songArtist={post.songArtist} songId={post.songId} loggedIn={true} accessToken={this.props.accessToken} />
                    </div>
                    <div className='likeButtonContainer'>
                        <button className='likeButton myButtonBlue' onClick={this.clickedLikePost}>Like</button>
                        <p>{this.state.likeCount}</p>
                    </div>
                </div>
            );
        } else {
            return (
                <div className="post">
                    <div className='postInner'>
                        <PostInfo name={post.name} text={post.text} />
                        <PostMusic songName={post.songName} songArtist={post.songArtist} songId={post.songId} loggedIn={false} />
                    </div>
                    <div className='likeButtonContainer'>
                        <button className='likeButton myButtonBlue' onClick={this.clickedLikePost}>Like</button>
                        <p>{this.state.likeCount}</p>
                    </div>
                </div>
            );
        }
    }
}
