import React, { Component } from 'react';
import queryString from 'query-string';
import '../style/App.css';
import { loadUserFollowing } from '../backendCalls'

class UserProfile extends Component {
    constructor() {
        super();
        this.state = {
            accessToken: '',
            username: ''
        };
        this.getFollowing = this.getFollowing.bind(this);
    }
    componentDidMount() {
        let accessToken = '';
        let username = '';
        let parsed = queryString.parse(window.location.search);
        if (parsed.access_token)
            accessToken = parsed.access_token;
        if (parsed.username)
            username = parsed.username;
        this.setState({
            accessToken: accessToken,
            username: username
        })
    }
    getFollowing() {
        loadUserFollowing(this.state.username);
    }
    render() {
        return (
            <div>
                <div className='header'>
                    <div>
                        <h1>Vibe Share</h1>
                    </div>
                    <div className='buttonContainer'>
                        <button className='myButtonWhite' onClick={this.handleLogInButton}>{this.state.accessToken ? 'Log Out' : 'Log In'}</button>
                    </div>
                </div>
                <button className='myButtonWhite' onClick={this.getFollowing}>button</button>

            </div>
        )
    }
}

export default UserProfile;
