import React, { Component } from 'react';
import profilePicturePlaceholder from '../img/profilePicturePlaceholder.jpg';
import '../style/App.css';
import queryString from 'query-string';
import { getTrack, playAt, pause, search } from '../spotifyCalls';

export default class NewPost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            text: '',
            searchFor: '',
            timeStart: '',
            filterString: '',
            searchResults: []
        };
    }
    updateResults = async (filterString) => {
        const results = await search(this.props.accessToken, filterString);
        this.setState({ searchResults: results.tracks.items });
        console.log(results.tracks.items);
    }

    render() {
        if (this.state.filterString !== '') {
            this.updateResults(this.state.filterString);
            // console.log(results);
            // this.setState({searchResults: results);
        }
        return (
            <div className="newPost">
                <Filter onTextChange={text => {
                    this.setState({ filterString: text });
                }} />
                {
                    this.state.searchResults.length > 0 &&
                    this.state.searchResults.map(currentSong => <h1>{currentSong.name}</h1>)
                }
                <h1>{this.state.filterString}</h1>
            </div>
        );
    }
}

class Filter extends Component {
    render() {
        return (
            <div className="filter">
                <input type="text" onKeyUp={event => this.props.onTextChange(event.target.value)} />
            </div>
        );
    }
}
