import React, { Component } from 'react';
import '../style/App.css';
import queryString from 'query-string';

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imgUrl: '',
            name: '',
        };
        this.redirectToHomePage = this.redirectToHomePage.bind(this);
        this.handleLogInButton = this.handleLogInButton.bind(this);
    }
    redirectToHomePage() {
        let parsed = queryString.parse(window.location.search);
        let accessToken;
        if (parsed.access_token)
            accessToken = parsed.access_token;

        window.location.href = window.location.origin + (accessToken ? ('?access_token=' + accessToken) : '');
    }
    handleLogInButton() {
        if (!queryString.parse(window.location.search).accessToken) {
            window.location.href = (window.location.href === 'http://localhost:3000/') ?
                'http://localhost:8000/login' : 'https://vibin-backend.herokuapp.com/login'
        }
    }
    render() {
        return (
            <div className='header'>
                <div>
                    <h1 onClick={this.redirectToHomePage}>Vibe Share</h1>
                    <h3 className='usernameTitle'>{this.props.username}</h3>
                </div>
                <div className='buttonContainer'>
                    <div className='mobileNav'>
                        {this.props.username !== '' && this.props.username !== 'Not logged in' &&
                            <div className='myButtonWhite' onClick={this.props.clickNewPost}>
                                {this.props.newPost ? "Cancel" : "New Post"}</div>}
                        <div className='myButtonWhite' onClick={this.handleLogInButton}>
                            {(this.props.username !== '' && this.props.username !== 'Not logged in') ?
                                'Log Out' : 'Log In'}</div>
                    </div>
                    <div className='desktopNav'>
                        {this.props.username !== '' && this.props.username !== 'Not logged in' &&
                            <button className='myButtonWhite' onClick={this.props.clickNewPost}>
                                {this.props.newPost ? "Cancel" : "New Post"}</button>}
                        <button className='myButtonWhite' onClick={this.handleLogInButton}>
                            {(this.props.username !== '' && this.props.username !== 'Not logged in') ?
                                'Log Out' : 'Log In'}</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default Header;
