import React, { Component } from 'react';
import profilePicturePlaceholder from '../img/profilePicturePlaceholder.jpg';
import '../style/App.css';
import queryString from 'query-string';
import Post from './Post';
import NewPost from './NewPost';
import { loadPosts, submitPost } from '../backendCalls';
import { getUser } from '../spotifyCalls';

class App extends Component {
  constructor() {
    super();
    this.state = {
      serverData: {},
      deviceId: '',
      accessToken: '',
      newPost: false
    };
    this.clickOuterNewPost = this.clickOuterNewPost.bind(this);
    this.submitNewPost = this.submitNewPost.bind(this);
  }

  handlePlayerStatus = async (device_id) => {
    this.setState({
      deviceId: device_id,
    });
  };

  initializePlayer = () => {
    const name = this.state.serverData.user.name + 'vibin';

    this.player = new window.Spotify.Player({
      getOAuthToken: cb => {
        cb(this.state.accessToken);
      },
      name
    });

    this.player.addListener('initialization_error', ({ message }) => { console.error(message); });
    this.player.addListener('authentication_error', ({ message }) => { console.error(message); });
    this.player.addListener('account_error', ({ message }) => { console.error(message); });
    this.player.addListener('playback_error', ({ message }) => { console.error(message); });

    // Playback status updates
    this.player.addListener('player_state_changed', state => { console.log(state); });

    // Ready
    this.player.addListener('ready', ({ device_id }) => {
      this.handlePlayerStatus(device_id);
      console.log('Ready with Device ID', device_id);
    });

    // Not Ready
    this.player.addListener('not_ready', ({ device_id }) => {
      console.log('Device ID has gone offline', device_id);
    });

    this.player.connect();
  };

  componentDidMount() {
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;
    let username = '';
    getUser(accessToken).then(user => {
      username = user.display_name;
    });
    loadPosts().then(post => this.setState({
      accessToken: accessToken,
      serverData: {
        posts: post,
        user: {
          name: username
        }
      }
    }));

    window.onSpotifyWebPlaybackSDKReady = this.initializePlayer();
  }

  async submitNewPost(text, songId, songName, songArtist, startTime, stopTime) {
    const user = this.state.serverData.user.name;
    const newPost = await submitPost(
      user,
      text,
      songId,
      songName,
      songArtist,
      startTime,
      stopTime
    );
    const updatedPosts = this.state.serverData.posts;
    updatedPosts.unshift(newPost);
    console.log("UPDATED POSTS");
    console.log(this.state.serverData.posts);
    this.setState({
      serverData: {
        posts: updatedPosts,
        user: this.state.serverData.user
      },
      newPost: false
    });

  }

  clickOuterNewPost() {
    const showPost = this.state.newPost;
    this.setState({
      newPost: !showPost
    });
  }

  render() {
    let allPosts =
      (this.state.serverData.user && this.state.serverData.posts) ?
        this.state.serverData.posts : [];

    return (
      <div className="App">
        {this.state.newPost &&
          <div>
            <div className="outerNewPost" onMouseDown={this.clickOuterNewPost}></div>
            <NewPost
              deviceId={this.state.deviceId}
              accessToken={this.state.accessToken}
              onMouseDown={() => { }}
              userName={this.state.serverData.user.name}
              submitNewPost={this.submitNewPost}
            />
          </div>
        }
        {
          (this.state.serverData.user && this.state.deviceId) &&
          <div className={this.state.newPost ? "blurPosts" : ""}>
            <h1>{this.state.serverData.user.name}</h1>
            <button onClick={() => {
              const showPost = this.state.newPost;
              this.setState({
                newPost: !showPost
              });
            }}>{this.state.newPost ? "Cancel" : "New Post"}</button>
            <div className="posts">
              {allPosts.map(currentPost =>
                <Post
                  post={currentPost}
                  deviceId={this.state.deviceId}
                  accessToken={this.state.accessToken}
                />)}
            </div>
          </div>
        }
      </div>
    );
  }
}

export default App;
