import React, { Component } from 'react';
import '../style/App.css';
import queryString from 'query-string';

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imgUrl: '',
            name: '',
        };
        this.redirectToUserProfile = this.redirectToUserProfile.bind(this);
    }
    componentDidMount() {
        let parsed = queryString.parse(window.location.search);
        if (parsed.access_token) {
            let accessToken = parsed.access_token;
            getLoggedInUser(accessToken).then(user => {
                this.setState({
                    name: user.display_name
                })
            });
        }
    }
    redirectToUserProfile() {
        let parsed = queryString.parse(window.location.search);
        let accessToken = ''
        if (parsed.access_token)
            accessToken = parsed.access_token;

        window.location.href = window.location.origin +
            '/user/?username=' + this.props.follow.username +
            '&userId=' + this.props.follow.userId +
            (accessToken ? ('&access_token=' + accessToken) : '');
    }
    render() {
        return (
            <div className='header'>
                <div>
                    <h1 onClick={this.redirectToHomePage}>Vibe Share</h1>
                    <h3 className='usernameTitle'>{name}</h3>
                </div>
                <div className='buttonContainer'>
                    {this.state.name !== '' && this.state.name !== 'Not logged in' &&
                        <button className='myButtonWhite' onClick={this.props.clickNewPost}>
                            {this.props.newPost ? "Cancel" : "New Post"}</button>
                    }
                    <button className='myButtonWhite' onClick={this.handleLogInButton}>{this.state.accessToken ? 'Log Out' : 'Log In'}</button>
                </div>
            </div>
        );
    }
}

export default Header;
